            case 1:
                return self::getGameStateValue('artifacts_mode') > 1;
            case 2:
                return self::getGameStateValue('cities_mode') > 1;
            case 3:
                return self::getGameStateValue('echoes_mode') > 1;
            // TODO(FIGURES): Add another case when we implement this expansion.
            default:
                return false;
        }
    }

