                $this_player_icon_counts = self::getPlayerResourceCounts($player_id);
                
                if ($this_player_icon_counts[5] < $min_factories &&
                    $this_player_icon_counts[1] < $min_crowns && 
                    $this_player_icon_counts[4] < $min_towers) {
                
